set names utf8;
DROP DATABASE IF EXISTS apple;
CREATE DATABASE apple charset=utf8;
USE apple;
create table mac_list (
    cid INT PRIMARY KEY AUTO_INCREMENT,
    mtitle varchar(32),
    price int,
    pic varchar(128),
    isnew boolean,
    screen varchar(64),
    cpu varchar(128),
    battery varchar(64),
    ssd varchar(64),
    touch varchar(64),
    keyboard varchar(32),
    weight varchar(32)
);

/*用户信息表*/
CREATE TABLE users(
    uid INT PRIMARY KEY AUTO_INCREMENT,
    uname VARCHAR(32),
    upwd VARCHAR(32),
    email VARCHAR(32)
);
/*购物车表*/
create table cart(
    id INT PRIMARY KEY AUTO_INCREMENT,
    uname varchar(32),
    coun INT,
    cid INT ,
    selection boolean,
    FOREIGN KEY(cid) REFERENCES mac_list(cid) 
);
